package Ejercicio10;

public class Ejercicio10 {

	public static void main(String[] args) {
		int suma=0;
		int producto=1;
		
		// TODO Auto-generated method stub
		
		for (int i=1; i<=10; i++)
		{
			suma= i + suma;
			System.out.println(suma);
			producto= i * producto;
			System.out.println(producto);
			
		}

	}

}
